import React from 'react';
import desktopImg from "../../images/desktop/image-header.jpg";
import mobileImg from "../../images/mobile/image-header.jpg";

const Hero = () => {
  return (
    <div className="hero relative h-screen w-screen">
      <picture className='absolute w-full inset-0 '>
      <source media='(min-width: 1024px )' srcSet={desktopImg}/> 
      <img src = {mobileImg}/>
    </picture>
    </div>
  )
}

export default Hero
