import React from 'react';
import logo from '../../images/logo.svg';
import hamburger from '../../images/icon-hamburger.svg';



const Navbar = () => {
  const [toggle, setToggle] = React.useState(false);

  const [windowSize, setWindowSize]= React.useState("");


  function changeImageResize () {
      setWindowSize({width : window.innerWidth, height : window.innerHeight});
  }
  // window is not from react that's hwy it requires useEffect
  React.useEffect(()=> {

      window.addEventListener("resize", changeImageResize)

      return () => {
          window.removeEventListener("resize", changeImageResize)
      }
  }, []);


  let navigation;
  if(windowSize.width < 768){
      return navigation =  <div className="nav-container absolute top-24 w-5/6 bg-white mx-auto left-10 z-10 transition-all delay-300">
        <ul className='nav-list flex flex-col justify-center items-center gap-10 p-5 relative' >
          <li><a className='text-gray-600 text-lg'>About</a></li>
          <li><a className='text-gray-600 text-lg'>Services</a></li>
          <li><a className='text-gray-600 text-lg'>Projects</a></li>
          <li><a className='text-gray-600 text-lg active'>Contact</a></li>
        </ul>
      </div>
    } else {
      return navigation = <div className="nav-container absolute top-24 w-5/6 bg-red-400 mx-auto z-10">
      <ul className='nav-list flex flex-col justify-center items-center gap-10 p-5 relative' >
        <li><a className='text-gray-600 text-lg'>About</a></li>
        <li><a className='text-gray-600 text-lg'>Services</a></li>
        <li><a className='text-gray-600 text-lg'>Projects</a></li>
        <li><a className='text-gray-600 text-lg active'>Contact</a></li>
      </ul>
    </div>
    }
    
      

  function toggleMenu () {
    setToggle(prevState => !prevState);
  }

  return (
    <nav className='flex justify-between align-center p-5 bg-transparent relative'>


      <div className="logo-container z-10">
        <img src = {logo} alt="logo sunny side"/>
      </div>


      {
        toggle && ``
      }


      <div className="toggle-container z-10 cursor-pointer" onClick={toggleMenu}>
        <img src={hamburger} alt = "hamburger button"/>
      </div>

    </nav>
  )
}

export default Navbar;
