import React from 'react';
import desktopImg from "../../images/desktop/image-header.jpg";
import mobileImg from "../../images/mobile/image-header.jpg";

const Hero = () => {
  return (
    <div className="hero">
      <picture>
        <source media='(min-width: 1024px )' srcSet={desktopImg}/> 
      </picture>
    </div>
  )
}

export default Hero
