import React from 'react';
import GalleryData from './GalleryData.js';
import GalleryItems from './GalleryItems.jsx';
import MobileCone from '../../images/mobile/image-gallery-cone.jpg';
import DesktopCone from '../../images/desktop/image-gallery-cone.jpg';


const Gallery = () => {
  const [windowSize, setWindowSize]= React.useState("");


  function changeImageResize () {
      setWindowSize({width : window.innerWidth, height : window.innerHeight});
  }
  // window is not from react that's hwy it requires useEffect
  React.useEffect(()=> {

      window.addEventListener("resize", changeImageResize)

      return () => {
          window.removeEventListener("resize", changeImageResize)
      }
  }, []);

  return (
    <>
      {
        windowSize > 768 ?
        <div className='grid md:grid-cols-4 lg:grid-cols-4'>
            <img src={DesktopCone}/>
            <img src={DesktopCone}/>
            <img src={DesktopCone}/>
            <img src={DesktopCone}/>
        </div>
        :
        <div className='grid grids-cols-2'>
          <img src={MobileCone}/>
          <img src={MobileCone}/>
          <img src={MobileCone}/>
          <img src={MobileCone}/>
      </div>
      }
    </>
  )
}

export default Gallery;
