import React from 'react';
import desktopImg from "../../images/desktop/image-header.jpg";
import mobileImg from "../../images/mobile/image-header.jpg";
import arrowDown from "../../images/icon-arrow-down.svg";

const Hero = () => {
  return (
    <div className="hero relative h-screen w-screen">
      <picture className='absolute w-full inset-0 '>
        <source media='(min-width: 1024px )' srcSet={desktopImg}/> 
        <img src = {mobileImg}/>
    </picture>

    <div className="nav-content flex flex-col items-center justify-center text-center justify-self-center row-gap-5 z-20 mx-auto basis-1/3">
        <h1 className='text-6xl text-white'>We Are Creative</h1>
        <div className="">
          <img src ={arrowDown} alt='arrow down'/>
        </div>
      </div>
    </div>
  )
}

export default Hero
