import React from 'react';
import logo from '../../images/logo.svg';
import hamburger from '../../images/icon-hamburger.svg';
import desktopImg from "../../images/desktop/image-header.jpg";
import mobileImg from "../../images/mobile/image-header.jpg";
import arrowDown from "../../images/icon-arrow-down.svg";

const Navbar = () => {
  const [toggle, setToggle] = React.useState(false);

  function toggleMenu () {
    setToggle(prevState => !prevState);
  }

  return (
    <nav className='flex justify-between align-center p-5 bg-transparent relative h-screen inset-0'>
      <picture className='absolute w-full inset-0'>
        <source media='(min-width: 1024px )' srcSet={desktopImg}/> 
        <img src = {mobileImg}/>
      </picture>

      <div className="logo-container z-10">
        <img src = {logo} alt="logo sunny side"/>
      </div>

      <div className="nav-content flex flex-col row-gap-5 z-20 mx-auto">
        <h1 className='text-6xl text-white'>We Are</h1>
        <h1 className='text-6xl text-white'>Creative</h1>
        <div className="">
          <img src ={arrowDown} alt='arrow down'/>
        </div>
      </div>

      {
        toggle && 
        <div className="nav-container absolute top-24 w-5/6 bg-white mx-auto left-10 z-10 transition-all delay-300">
        <ul className='nav-list flex flex-col justify-center items-center gap-10 p-5 relative' >
          <li><a className='text-gray-600 text-lg'>About</a></li>
          <li><a className='text-gray-600 text-lg'>Services</a></li>
          <li><a className='text-gray-600 text-lg'>Projects</a></li>
          <li><a className='text-gray-600 text-lg active'>Contact</a></li>
        </ul>
      </div>
      }

      <div className="toggle-container z-10" onClick={toggleMenu}>
        <img src={hamburger} alt = "hamburger button"/>
      </div>

    </nav>
  )
}

export default Navbar;
